mapper.select:
SELECT * FROM ( SELECT @rownum := @rownum + 1 AS RNUM , SEQ AS SEQ_NO , CASE CTGR WHEN '1' THEN 'IMS_BM_FAQ_0018' WHEN '2' THEN 'IMS_BM_FAQ_0001' WHEN '3' THEN 'IMS_BM_FAQ_0002' WHEN '4' THEN 'IMS_BM_FAQ_0019' WHEN '5' THEN 'IMS_BM_FAQ_0020' WHEN '6' THEN 'IMS_BM_FAQ_0004' WHEN '7' THEN 'IMS_BM_FAQ_0005' END CTGR , TITLE , BODY , CASE NOTI_TYPE WHEN '1' THEN 'IMS_BM_FAQ_0022' WHEN '2' THEN 'IMS_BM_FAQ_0023' WHEN '3' THEN 'IMS_BM_FAQ_0024' END NOTI_TYPE , CASE DEL_FLG WHEN '0' THEN 'IMS_BM_FAQ_0006' WHEN '1' THEN 'IMS_BM_FAQ_0007' END DEL_FLG , REG_DT , WORKER FROM TB_FAQ, (SELECT @rownum := 0)R WHERE 1=1
AND CTGR = #{BOARD_TYPE}
AND TITLE LIKE '%'||#{TITLE}||'%'
ORDER BY SEQ_NO DESC
)TBL WHERE RNUM BETWEEN #{intPageStart} AND #{intPageEnd}

SELECT COUNT(1) FROM ( SELECT SEQ AS SEQ_NO , CASE CTGR WHEN '1' THEN 'IMS_BM_FAQ_0018' WHEN '2' THEN 'IMS_BM_FAQ_0001' WHEN '3' THEN 'IMS_BM_FAQ_0002' WHEN '4' THEN 'IMS_BM_FAQ_0019' WHEN '5' THEN 'IMS_BM_FAQ_0020' WHEN '6' THEN 'IMS_BM_FAQ_0004' WHEN '7' THEN 'IMS_BM_FAQ_0005' END CTGR , TITLE , BODY , CASE NOTI_TYPE WHEN '1' THEN 'IMS_BM_FAQ_0022' WHEN '2' THEN 'IMS_BM_FAQ_0023' WHEN '3' THEN 'IMS_BM_FAQ_0024' END NOTI_TYPE , CASE DEL_FLG WHEN '0' THEN 'IMS_BM_FAQ_0006' WHEN '1' THEN 'IMS_BM_FAQ_0007' END DEL_FLG , REG_DT , WORKER FROM TB_FAQ WHERE 1=1
AND CTGR = #{BOARD_TYPE}
AND TITLE LIKE '%'||#{TITLE}||'%'
ORDER BY SEQ_NO DESC
)TBL

mapper.update:

UPDATE TB_FAQ 
SET  CTGR 	    = #{CTGR}
   , TITLE      = #{TITLE}
   , BODY       = #{BODY}
   , NOTI_TYPE  = #{NOTI_TYPE}
   , WORKER     = #{WORKER}
WHERE SEQ = #{SEQ_NO}
