mapper.update:

            UPDATE TB_BO_USR_ACCT 
            SET PSWD = #{NEWPSWD}
               ,UPD_DT = (SELECT DATE_FORMAT(NOW(), "%Y%m%d%H%i%s") FROM DUAL)
               ,OLD_PW1 = #{PSWD}
               ,OLD_PW2 = #{OLD_PW1}
			   ,OLD_PW3 = #{OLD_PW2}
			   ,OLD_PW4 = #{OLD_PW3}
			   ,OLD_PW5 = #{OLD_PW4}
            WHERE  USR_ID = #{USR_ID}
            AND    PSWD   = #{PSWD}


            UPDATE TB_BO_USR_ACCT 
			SET FAIL_CNT = (SELECT A.FAIL_CNT + 1 FROM (SELECT FAIL_CNT FROM TB_BO_USR_ACCT WHERE USR_ID = #{USR_ID}) A)
			WHERE USR_ID = #{USR_ID}


            UPDATE TB_BO_USR_ACCT 
			SET FAIL_CNT = 0
			WHERE USR_ID = #{USR_ID}

mapper.insert:

			INSERT INTO TB_CO(
			   CO_NO
			  ,CONT_NM 
         	  ,CONT_TEL
         	  ,CONT_EMAIL
         	  ,ADDR_NO1
         	  ,MEMO
         	  ,HOSTING_CD
         	  ,RECV_DT
			) VALUES (
			  (SELECT A.CO_NO FROM (SELECT CONCAT('REQ', IF(EXISTS(SELECT * FROM TB_CO)=0, 1, LAST_INSERT_ID()+1)) AS CO_NO ) A)
         	  ,#{CONT_NM}
         	  ,#{CONT_TEL}
         	  ,#{CONT_EMAIL}
         	  ,#{ADDRESS}
         	  ,#{MEMO}
         	  ,'2'
         	  ,DATE_FORMAT(NOW(), "%Y%m%d")
			)

mapper.select:

        SELECT CO_NO 
        FROM TB_CO
		WHERE CO_NO = #{CO_NO}
