mapper.select:
SELECT * FROM ( SELECT @rownum:=@rownum+1 AS RNUM, X.* FROM ( SELECT MENU_NO ,MENU_GRP_NO ,MENU_NM ,MENU_LINK ,MENU_URI_SGMNT ,MENU_SEQ ,STATUS ,REG_DT ,CASE WHEN UPD_DT IS NULL THEN ' ' ELSE UPD_DT END AS UPD_DT ,WORKER ,WORKER_IP ,PARENT_MENU_NO ,(SELECT MENU_NM FROM TB_BO_MENU WHERE MENU_NO = A.PARENT_MENU_NO) PARENT_MENU_NM FROM TB_BO_MENU A WHERE STATUS NOT IN ('9')
AND MENU_GRP_NO = ${MENU_GRP_NO}
AND MENU_NO = ${MENU_NO}
ORDER BY MENU_GRP_NO, MENU_SEQ )X , (SELECT @rownum:=0) tmp )TBL WHERE RNUM BETWEEN ${intPageStart} AND ${intPageEnd}

mapper.update:

UPDATE TB_BO_MENU
   SET MENU_SEQ = ${MENU_SEQ}
      ,STATUS = #{STATUS}
      ,UPD_DT = DATE_FORMAT(NOW(), "%Y%m%d%H%i%s")
      ,WORKER = #{WORKER}
      ,WORKER_IP = #{WORKER_IP}
 WHERE MENU_NO = ${MENU_NO}
 AND   MENU_TYPE = '2'

mapper.insert:

SELECT COALESCE(MAX(MENU_SEQ),0) + 1 FROM TB_BO_MENU
WHERE MENU_GRP_NO = ${MENU_GRP_NO}


INSERT INTO TB_BO_MENU (MENU_GRP_NO, MENU_NM, MENU_TYPE, MENU_LINK, MENU_URI_SGMNT
                       ,MENU_SEQ, STATUS, WORKER, WORKER_IP, REG_DT)
                 VALUES(${MENU_GRP_NO}, #{MENU_NM}, #{MENU_TYPE}, #{MENU_LINK}, #{MENU_URI_SGMNT}
                       ,${C_MENU_SEQ}, #{STATUS}, #{WORKER}, #{WORKER_IP}, now()+0)

mapper.update:

UPDATE TB_BO_MENU
   SET MENU_NM = #{MENU_NM}
      ,MENU_LINK = #{MENU_LINK}
      ,MENU_URI_SGMNT = #{MENU_URI_SGMNT}
      ,MENU_SEQ = ${MENU_SEQ}
      ,STATUS = #{STATUS}
      ,UPD_DT = DATE_FORMAT(NOW(), "%Y%m%d%H%i%s")
      ,WORKER = #{WORKER}
      ,WORKER_IP = #{WORKER_IP}
      ,PARENT_MENU_NO = #{PARENT_MENU_NO}
 WHERE MENU_NO = ${MENU_NO}

mapper.delete:

UPDATE TB_BO_MENU
SET    STATUS = '9'
WHERE  MENU_NO = ${MENU_NO}

mapper.update:

UPDATE TB_BO_MENU  
SET    MENU_SEQ = MENU_SEQ - 1  
WHERE  MENU_GRP_NO = ${MENU_GRP_NO}
AND    MENU_SEQ > ${MENU_SEQ}
AND    STATUS NOT IN ('9')
AND    MENU_TYPE = '2'
