mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY A.USR_ID) AS RNUM ,A.USR_ID ,A.USR_NM ,A.USR_TYPE ,A.AUTH_NO ,CASE WHEN A.TEL_NO IS NULL THEN ' ' ELSE A.TEL_NO END AS TEL_NO ,CASE WHEN A.EMAIL IS NULL THEN ' ' ELSE A.EMAIL END AS EMAIL ,A.STATUS ,A.REG_DT ,A.UPD_DT ,A.WORKER ,A.WORKER_IP ,B.AUTH_NM FROM TB_BO_USR_ACCT A INNER JOIN TB_BO_USR_AUTH B ON A.AUTH_NO = B.AUTH_NO WHERE 1=1
AND A.USR_ID LIKE '%'||#{USR_ID}||'%'
AND A.USR_NM LIKE '%'||#{USR_NM}||'%'
AND A.AUTH_NO = ${AUTH_NO}
AND A.STATUS = #{STATUS}
AND A.USR_TYPE = #{USR_TYPE}
AND A.USR_ID = #{DTL_USR_ID}
)TBL WHERE RNUM BETWEEN ${intPageStart} AND ${intPageEnd} ORDER BY REG_DT DESC


SELECT COALESCE(COUNT(1),0)
FROM TB_BO_USR_ACCT A
INNER JOIN TB_BO_USR_AUTH B ON A.AUTH_NO = B.AUTH_NO
WHERE 1=1




     AND A.USR_ID LIKE '%'||#{USR_ID}||'%' 




     AND A.USR_NM LIKE '%'||#{USR_NM}||'%'




     AND A.AUTH_NO = ${AUTH_NO} 




     AND A.STATUS = #{STATUS}




     AND A.USR_TYPE = #{USR_TYPE}

mapper.insert:

            INSERT INTO TB_BO_USR_ACCT_MER (USR_ID, I_MID, WORKER, WORKER_IP)
			SELECT #{USR_ID}, COLUMN_VALUE, #{WORKER}, #{WORKER_IP}
			FROM TABLE(UF_GET_SPLIT(#{MERCHANTIDLIST}, ','))

mapper.update:

UPDATE TB_BO_USR_ACCT 
SET PSWD   = #{USRNEWPSWD}
   ,UPD_DT = DATE_FORMAT(NOW(), "%Y%m%d%H%i%s")
   ,FAIL_CNT = 0
   ,EMAIL = #{EMAIL}
WHERE USR_ID = #{USR_ID}

mapper.select:

SELECT COALESCE(COUNT(USR_ID),0)
FROM  TB_BO_USR_ACCT    
WHERE USR_ID = #{USR_ID}
AND   PSWD   = #{PSWD}

mapper.update:

            UPDATE TB_BO_USR_ACCT
			SET    USR_NM  = #{USR_NM}
			      ,AUTH_NO = ${AUTH_NO}
			      ,TEL_NO  = #{TEL_NO}
			      ,EMAIL   = #{EMAIL}
			      ,STATUS  = #{STATUS}
			      ,UPD_DT  = DATE_FORMAT(NOW(), "%Y%m%d%H%i%s")
			      ,WORKER  = #{WORKER}
			      ,WORKER_IP = #{WORKER_IP}
			WHERE  USR_ID = #{USR_ID}
