mapper.select:
SELECT TBL.NO ,TBL.SEQ ,TBL.BODY ,TBL.TITLE ,DATE_FORMAT(TBL.REG_DNT, '%Y-%m-%d %H:%i:%s') AS REG_DT ,TBL.WORKER ,TBL.NOTI_CD1 ,TBL.NOTI_CD ,TBL.NOTI_TRG_CD1 ,TBL.NOTI_TRG_CD ,TBL.NOTI_ST_CD1 ,TBL.NOTI_ST_CD FROM ( SELECT @rownum := @rownum+1 AS NO ,SEQ , SUBSTR(BODY, 1, 4000) AS BODY , TITLE , REG_DNT , WORKER , (SELECT DESC1 FROM TB_CODE WHERE CODE_CL='0027' AND USE_FLG = '1' AND CODE1=NOTI_CD) AS NOTI_CD1 , NOTI_CD , (SELECT DESC1 FROM TB_CODE WHERE CODE_CL='0028' AND USE_FLG = '1' AND CODE1=NOTI_TRG_CD) AS NOTI_TRG_CD1 , NOTI_TRG_CD, (SELECT DESC1 FROM TB_CODE WHERE COL_NM='NOTI_ST_CD' AND USE_FLG = '1' AND CODE1= NOTI_ST_CD) AS NOTI_ST_CD1, NOTI_ST_CD FROM TB_NOTICE, (SELECT @rownum:=0) R WHERE REG_DT BETWEEN #{frDt} AND #{toDt} AND NOTI_TRG_CD ='01' AND NOTI_CD !='10'
AND NOTI_CD =#{BOARD_TYPE}
ORDER BY SEQ DESC
) TBL
WHERE NO BETWEEN ${intPageStart} AND ${intPageEnd} ORDER BY NO ASC

SELECT COUNT(1) FROM ( SELECT @rownum := @rownum+1 AS NO ,SEQ , SUBSTR(BODY, 1, 4000) AS BODY , TITLE , REG_DT , WORKER , (SELECT DESC1 FROM TB_CODE WHERE CODE_CL='0027' AND USE_FLG = '1' AND CODE1=NOTI_CD) AS NOTI_CD1 , NOTI_CD , (SELECT DESC1 FROM TB_CODE WHERE CODE_CL='0028' AND USE_FLG = '1' AND CODE1=NOTI_TRG_CD) AS NOTI_TRG_CD1 , NOTI_TRG_CD, (SELECT DESC1 FROM TB_CODE WHERE COL_NM='NOTI_ST_CD' AND USE_FLG = '1' AND CODE1= NOTI_ST_CD) AS NOTI_ST_CD1, NOTI_ST_CD FROM TB_NOTICE, (SELECT @rownum:=0) R WHERE REG_DT BETWEEN #{frDt} AND #{toDt} AND NOTI_TRG_CD ='01' AND NOTI_CD !='10'
AND NOTI_CD =#{BOARD_TYPE}
ORDER BY SEQ DESC
) TBL
ORDER BY NO ASC
